import graph;

// Set unit size
unitsize(1cm);

// Define the viewing range
real xmax = 11;
real ymax = 11;

// Define parameters a and b
real a = 4;
real b = 5;

// Draw the axes
draw((-xmax,0)--(xmax,0), black+1.2bp, Arrow(TeXHead));
label("$x$", (xmax,0), SW, fontsize(10pt));

draw((0,-ymax)--(0,ymax), black+1.2bp, Arrow(TeXHead));
label("$y$", (0,ymax), SW, fontsize(10pt));

// Add ticks and labels to the axes
for(int i=-10; i<=10; i+=2) {
  if(i != 0) {
    draw((i,0.2)--(i,-0.2), black+1.2bp);
    label(string(i), (i,-0.2), S, fontsize(8pt));

    draw((0.2,i)--(-0.2,i), black+1.2bp);
    label(string(i), (-0.2,i), W, fontsize(8pt));
  }
}
// Origin label
label("0", (0,0), SW, fontsize(8pt));

// Draw the dotted central rectangle
draw((-b,-a)--(-b,a)--(b,a)--(b,-a)--cycle, cyan+1.5bp+dotted);

// Draw the dashed asymptotes
draw((-xmax*0.9, a/b*xmax*0.9)--(xmax*0.9, -a/b*xmax*0.9), 
     orange+dashed+1.2bp, Arrows(TeXHead));
draw((-xmax*0.9, -a/b*xmax*0.9)--(xmax*0.9, a/b*xmax*0.9), 
     orange+dashed+1.2bp, Arrows(TeXHead));

// Draw the hyperbola
real xmin = -sqrt(ymax^2 - a^2)*b/a;
real xmax_hyp = sqrt(ymax^2 - a^2)*b/a;

// Upper branch
path upper = graph(new real(real x) {
  return sqrt(a*a*(1 + x*x/(b*b)));
}, xmin, xmax_hyp, n=200);

// Lower branch  
path lower = graph(new real(real x) {
  return -sqrt(a*a*(1 + x*x/(b*b)));
}, xmin, xmax_hyp, n=200);

draw(upper, blue+1.5bp, Arrows(TeXHead));
draw(lower, blue+1.5bp, Arrows(TeXHead));

