import graph;

// Set up the figure
size(16cm);
defaultpen(fontsize(10pt));

// --- DEFINE COORDINATES ---
pair R = (0,0);
pair A = (10,0);
pair M = (10,10);
pair N = (20,10);
pair O = (25,0);
pair P = (20,-10);
pair Q = (10,-10);
pair K = (0,7);
pair L = (7,7);

// --- ANGLE MARKER FUNCTION ---
void drawAngleMarker(pair A, pair B, pair C, real radius=1.5) {
    // Calculate vectors from vertex B
    pair BA = unit(A - B);
    pair BC = unit(C - B);

    // Calculate angles in degrees
    real angleBA = degrees(atan2(BA.y, BA.x));
    real angleBC = degrees(atan2(BC.y, BC.x));

    // Ensure proper arc direction
    if (angleBC < angleBA) angleBC += 360;

    // Draw the arc
    draw(arc(B, radius, angleBA, angleBC), black+0.8bp);
}

// --- DRAW THE MAIN SHAPES ---
// Hexagon RMNOPQ
draw(R--M--N--O--P--Q--cycle, black+1bp);

// Rectangle KL
draw(K--L, black+1bp);

// Rectangle edge RK
draw(R--K, black+1bp);

// Dashed line AO
draw(A--O, dashed);

// Line RA
draw(R--A, black+1bp);

// Perpendicular line MA
draw(M--A, black+1bp);

// --- RIGHT ANGLE MARKER AT A ---
pair MA = M - A;
pair RA = R - A;
pair uMA = unit(MA);
pair uRA = unit(RA);
real r = 1.2;
pair p1 = A + uMA * r;
pair p2 = A + uMA * r + uRA * r;
pair p3 = A + uRA * r;
draw(p1--p2--p3, black+0.8bp);

// --- ADD ANGLE MARKERS ---
drawAngleMarker(K, L, R);  // Angle KLR at L
drawAngleMarker(A, R, M);  // Angle MRA at R

// --- TICK MARKS ---
// Helper function for tick marks
void mytick(pair A, pair B, real t=0.5, real len=0.7) {
  pair dir = unit(B-A);
  pair norm = rotate(90)*dir;
  pair mid = A + t*(B-A);
  draw(mid - norm*len/2 -- mid + norm*len/2, black+1bp);
}

// Tick marks on MN, QP, and MA
