import geometry;

// Set up the figure
size(8cm);

// Define parameters and scale
real radius = 2.5;
real scale = 1.5;
real angleE = 60;  // Angle for point E
real angleG = 195; // Angle for point G

// Apply scaling
radius *= scale;

// Define coordinates using polar coordinates
pair F = (0, 0); // Center
pair D = radius * dir(0);
pair E = radius * dir(angleE);
pair G = radius * dir(angleG);

// Function to create a sector path
path sector(pair center, real r, real startAngle, real endAngle) {
    return center -- arc(center, r, startAngle, endAngle) -- cycle;
}

// Draw fills and outlines (painter's algorithm)
// First, fill the entire circle with light blue
fill(circle(F, radius), cyan + opacity(0.4));

// Next, fill the 60-degree sector with light orange/salmon color
fill(sector(F, radius, 0, angleE), orange + opacity(0.7));

// Draw the main circle outline
draw(circle(F, radius), black + 1.5);

// Draw the two radii that form the sector
draw(F -- D, black + 1.5);
draw(F -- E, black + 1.5);

// Add points (black dots)
real dotSize = 0.05pt * scale;
fill(circle(F, dotSize), black);
fill(circle(G, dotSize), black);

// Add text labels for the points
label("E", E, N, fontsize(12pt));
label("D", D, SE, fontsize(12pt));
label("G", G, W, fontsize(12pt));
label("F", F, S, fontsize(12pt));

// Add measurement labels
// The "10 in." label along radius FE, sloped to match its angle
pair labelPos = F + 0.4 * (E - F); // 40% of the way from F to E
real labelAngle = degrees(atan2(E.y - F.y, E.x - F.x));
label("10 in.", labelPos, dir(90 + labelAngle), fontsize(10pt));

// The "60°" label inside the sector at the bisecting angle (30 degrees)
pair anglePos = 0.5 * radius * dir(30);
label("60°", anglePos, fontsize(14pt));

