import geometry;
size(300, 200);

// --- Define Coordinates ---
// Place the intersection point M at the origin
pair M = (0,0);

// Define points for the horizontal line L-N
pair L = (-3.5,0);
pair N = (3.5,0);

// Define points for the transversal line K-O using polar coordinates
// format: radius * dir(angle)
pair K = 3 * dir(140);
pair O = 3 * dir(-40); // Same as 3 * dir(140-180)
pair S = 3 * dir(45);

// --- Draw Lines and Points ---
// Define a pen for the line style to be reused
pen lineStyle = linewidth(1.2);

// Draw the two intersecting lines with arrows on both ends
// The arrow style is passed as a separate argument
draw(L--N, lineStyle, Arrows(6));
draw(K--O, lineStyle, Arrows(6));

// Draw the ray from M to S with an arrow at the end
draw(M--S, lineStyle, Arrow(6));

// Place a filled circle at point M
dot(M, linewidth(4));

// --- Add Labels ---
// Asymptote uses directional arguments (N, S, E, W, NW, SE, etc.)
// for label placement relative to the point.
label("M", M, (0,-2)); // Label "M" South (below) of point M
label("2", M, (0,3)); // Label "2" North (above) of point M

label("L", L, W); // Label "L" West (left) of point L
label("N", N, E); // Label "N" East (right) of point N
label("K", K, NW); // Label "K" North-West (above left) of K
label("O", O, SE); // Label "O" South-East (below right) of O

