// --- Right Angle Markers ---
void rightangle(pair O, pair P, pair Q, real size=0.18)
{
  // Draws a right angle marker at point P between OP and QP
  pair v1 = unit(O-P), v2 = unit(Q-P);
  pair pa = P + size*v1;
  pair pb = pa + size*v2;
  pair pc = P + size*v2;
  draw(pa--pb--pc, black+linewidth(0.8));
}

// At A: angle BAC (C--A--B)
rightangle(C, A, B, 0.18);
// At B: angle ABD (A--B--D)
rightangle(A, B, D, 0.18);
// At D: angle EDC (E--D--C)
rightangle(E, D, C, 0.18);

// --- Angle Arcs and Labels ---
void anglearc(pair O, pair A, pair B, real r=0.35, string lbl="", real lblshift=0.18, pen p=black)
{
  // Draws an angle arc at O from OA to OB, with optional label
  real t1 = degrees(dir(A-O));
  real t2 = degrees(dir(B-O));
  real sweep = t2-t1;
  if(sweep <= 0) sweep += 360;
  path arcpath = arc(O, r, t1, t1+sweep);
  draw(arcpath, p);
  if(lbl != "") {
    real midang = t1 + sweep/2;
    pair labelpos = O + (r+lblshift)*dir(midang);
    label(lbl, labelpos, fontsize(9pt));
  }
}

// θ₂ at C: angle DCE
anglearc(C, D, E, 0.7, "$\theta_2$", 0.18);

// θ₁ at B: angle CBA
anglearc(B, C, A, 0.5, "$\theta_1$", 0.25);

