// 1. Draw the Plane M
// Define coordinates for the four corners of the parallelogram
pair P1 = (0, 0);
pair P2 = (8, 0);
pair P3 = (10, 3);
pair P4 = (2, 3);

// Draw and fill the plane with a light blue color
path plane = P1 -- P2 -- P3 -- P4 -- cycle;
fill(plane, lightcyan);
draw(plane);

// Label the plane in the top-right corner
label("$M$", (9.4, 2.8), fontsize(10pt));

// 2. Define and Draw Lines p and q
// Define the start and end points that determine the lines
pair q_start = (1.5, 0.05);
pair q_end = (7.5, 2.95);
pair p_start = (1.6, 2.3);
pair p_end = (3.5, 0);

// Draw the lines with arrows at both ends
draw(q_start -- q_end, Arrows(5pt));
draw(p_start -- p_end, Arrows(5pt));

// Label the lines
label("$p$", (2.1, 2.2), fontsize(10pt));
label("$q$", (7.7, 2.7), fontsize(10pt));

// 3. Find Intersection and Place Points
// Function to find intersection of two lines using parametric form
pair intersectionPoint(pair A1, pair A2, pair B1, pair B2) {
    // Line 1: A1 + t*(A2-A1), Line 2: B1 + s*(B2-B1)
    // Solving: A1 + t*(A2-A1) = B1 + s*(B2-B1)
    real dx1 = A2.x - A1.x;
    real dy1 = A2.y - A1.y;
    real dx2 = B2.x - B1.x;
    real dy2 = B2.y - B1.y;
    real dx3 = A1.x - B1.x;
    real dy3 = A1.y - B1.y;

    real denom = dx1*dy2 - dy1*dx2;
    if (abs(denom) < 1e-10) {
        // Lines are parallel, return midpoint as fallback
        return ((A1 + A2) + (B1 + B2))/4;
    }

    real t = (dx2*dy3 - dy2*dx3) / denom;
    return A1 + t * (A2 - A1);
}

// Find intersection point H
pair H = intersectionPoint(q_start, q_end, p_start, p_end);

// Define coordinates for other points along the lines
pair J = q_start + 0.05 * (q_end - q_start);
pair K = q_start + 0.5 * (q_end - q_start);
pair G = p_start + 0.9 * (p_end - p_start);

// Define the coordinate for point L, which is independent of the lines
pair L = (7.7, 1.2);

// 4. Draw and Label the Points
// Function to draw a point (black circle)
void drawPoint(pair pos, string label_text, pair label_dir = N) {
    dot(pos, linewidth(3pt));
    if (label_text != "") {
        label(label_text, pos, label_dir, fontsize(10pt));
    }
}

// Draw each point as a black circle and add its label
drawPoint(J, "$J$", NW);
drawPoint(G, "$G$", N);
drawPoint(H, "$H$", N);
drawPoint(K, "$K$", N);
drawPoint(L, "$L$", E);

