import three;
size(8cm);

// Define cuboid dimensions (scaled to match TiKz code)
real L = 8;    // Length (corresponds to 30 cm label)
real W = 4.5;  // Width  (corresponds to 8 cm label)
real H = 7;    // Height (corresponds to 12 cm label)

// Define the 8 vertices of the cuboid
triple A    = (0,0,0);           // front-bottom-left
triple vx   = (L,0,0);
triple vy   = (0,W,0);
triple vz   = (0,0,H);
triple vxy  = (L,W,0);
triple vxz  = (L,0,H);
triple vyz  = (0,W,H);
