import geometry;

// Set up the figure
unitsize(0.8cm);
defaultpen(fontsize(14pt));

// Define coordinates before rotation
pair M = (0, 0);
pair J = (-6, 0);
pair L = (4, 0);
pair K = (0, sqrt(24));

// Apply rotation of -40 degrees
transform rot = rotate(-40);
M = rot * M;
J = rot * J;
L = rot * L;
K = rot * K;

// Define custom pens
pen line_pen = linewidth(1.5);
pen label_pen = blue;
pen symbol_pen = rgb(0.7, 0.2, 0.2) + linewidth(1.5); // red!70!black equivalent

// Draw the main triangle JKL and the altitude KM
draw(J -- K -- L -- cycle, line_pen);
draw(K -- M, line_pen);

// Vertex labels
label("J", J, NW);
label("K", K, NE);
label("L", L, SE);
label("M", M, SW);

// Length labels
label("6", (J + M)/2, SW, label_pen);
label("4", (M + L)/2, SW, label_pen);
label("x", (K + M)/2, E, label_pen);

// Right angle symbols
real symbolsize = 0.4;

// Right angle symbol at M (for angle KMJ)
// We need to find the perpendicular directions at M
pair MJ_unit = unit(J - M);
pair MK_unit = unit(K - M);

// Create the right angle marker
pair M1 = M + symbolsize * MJ_unit;
pair M2 = M + symbolsize * MK_unit;
pair M3 = M1 + M2 - M;
draw(M1 -- M3 -- M2, symbol_pen);

// Right angle symbol at K (for angle JKL)
// Calculate unit vectors along KJ and KL
pair KJ_unit = unit(J - K);
pair KL_unit = unit(L - K);

// Calculate the corner points of the square symbol
pair P1 = K + symbolsize * KJ_unit;
pair P2 = K + symbolsize * KL_unit;
pair P3 = P1 + P2 - K;

// Draw the two sides of the square symbol
draw(P1 -- P3 -- P2, symbol_pen);

