// --- 1. Coordinates ---
// Scale factor to match TikZ scale=0.2
real scale_factor = 0.2;

// Define coordinates (scaled by 0.2 to match TikZ)
pair R = scale_factor * (0, 0);
pair S = scale_factor * (-12, 0);
pair T = scale_factor * (-24, 0);
pair W = scale_factor * (-40, 0);

pair Q = scale_factor * (0, 16);
pair P = scale_factor * (-12, 16);
pair O = scale_factor * (-40, 16);

pair U = scale_factor * (-24, -16);
pair V = scale_factor * (-40, -16);

// --- 2. Drawing the Figure ---
// Draw the outer perimeter of the entire shape
draw(O--Q--R--S--T--U--V--W--cycle);

// Draw the internal lines
draw(P--T);
draw(Q--S);
draw(W--T);

// --- 3. Add Markings ---
// Function to draw vertical tick marks (horizontal line)
void drawVerticalTick(pair A, pair B, real tick_size = 0.32) {
    pair mid = (A + B) / 2;
    draw((mid.x - tick_size, mid.y)--(mid.x + tick_size, mid.y));
}

// Function to draw horizontal tick marks (vertical line)
void drawHorizontalTick(pair A, pair B, real tick_size = 0.32) {
    pair mid = (A + B) / 2;
    draw((mid.x, mid.y - tick_size)--(mid.x, mid.y + tick_size));
}

// Add single tick marks to the specified segments
drawVerticalTick(O, W);
drawVerticalTick(W, V);
drawHorizontalTick(V, U);
drawVerticalTick(U, T);
drawVerticalTick(Q, R);

// Additional tick marks from the code
drawHorizontalTick(R, Q);
drawHorizontalTick(V, W);
drawHorizontalTick(U, T);

// Function to draw right angle marker
void drawRightAngle(pair vertex, pair dir1, pair dir2, real size = 0.32) {
    pair corner1 = vertex + size * unit(dir1);
    pair corner2 = vertex + size * unit(dir2);
    pair corner3 = vertex + size * unit(dir1) + size * unit(dir2);
    draw(corner1--corner3--corner2);
    // Fill with white background
    fill(corner1--vertex--corner2--corner3--cycle, white);
    draw(corner1--corner3--corner2);
}

// Add the right-angle symbol at vertex R
drawRightAngle(R, Q - R, S - R);

// --- 4. Add Labels and Dimensions ---
// Add vertex labels with appropriate positioning
label("O", O, NW);
label("P", P, N);
label("Q", Q, NE);
label("R", R, SE);
label("S", S, SE);
label("T", T, SE);
label("U", U, SW);
label("V", V, SW);
label("W", W, NW);

// Add the dimension "40" above the top edge
pair dim_start = O + scale_factor * (0, 3);
pair dim_end = Q + scale_factor * (0, 3);
draw(dim_start--dim_end);
// Draw end marks
real mark_size = 0.08;
draw((dim_start.x, dim_start.y - mark_size)--(dim_start.x, dim_start.y + mark_size));
draw((dim_end.x, dim_end.y - mark_size)--(dim_end.x, dim_end.y + mark_size));
label("40", (dim_start + dim_end)/2, N);

// Add the length "20" to the diagonal QS
draw(Q--S);
label("20", (-1.5,1.5), N);

// Add the length "12" below the segment SR
label("12", (-1,-0.2), S);

// Add the variables x and y with their tick marks
// The dimension line for x has a tick in the middle
drawHorizontalTick(W, T);
label("x", (W + T)/2 + scale_factor * (0, 2), N);

// The segment TS is labeled with y
label("y", (T + S)/2 + scale_factor * (0, 2), N);

// Add the "Given" text box to the right of the figure
pair given_pos = Q + scale_factor * (15, -6);
label("Given:", given_pos, W);
label("$\overline{OQ} \cong \overline{WR}$", given_pos + scale_factor * (0, -2), W);
label("$\overline{PQ} \cong \overline{TS}$", given_pos + scale_factor * (0, -4), W);

