// --- Dimension lines function ---
void dimline(pair A, pair B, string label="", pair labelshift=(0,0), pen p=black)
{
  real ticklen = 0.15;
  pair dir = unit(B-A);
  pair n = rotate(90)*dir;
  draw(A-ticklen*n--A+ticklen*n, p);
  draw(B-ticklen*n--B+ticklen*n, p);
  draw(A--B, p);
  if(label != "")
    label(label, (A+B)/2 + labelshift, UnFill);
}

