// Define parameters for the plot
real cuspX = 1.2;
real targetArrowX = 3.1;
real targetArrowY = 5.0;

// Calculate the scaling factor A
real baseForPower = targetArrowX - cuspX;
real scalingFactorA;
if (baseForPower > 0) {
    real powerResult = baseForPower^1.5;
    scalingFactorA = targetArrowY / powerResult;
} else {
    scalingFactorA = 1; // Default value
}

// Function for the upper and lower branches
real f_upper(real x) {
    if (x >= cuspX) {
        return scalingFactorA * (x - cuspX)^1.5;
    } else {
        return 0;
    }
}

real f_lower(real x) {
    if (x >= cuspX) {
        return -scalingFactorA * (x - cuspX)^1.5;
    } else {
        return 0;
    }
}

