// Draw angles
// Function to draw angle arc
void drawAngle(pair A, pair O, pair B, string label, real r, pen p) {
    real angleA = degrees(A - O);
    real angleB = degrees(B - O);
    if (angleA < 0) angleA += 360;
    if (angleB < 0) angleB += 360;

    // Ensure we draw the smaller angle
    if (abs(angleB - angleA) > 180) {
        if (angleA > angleB) angleB += 360;
        else angleA += 360;
    }

    // Draw the arc
    path anglePath = arc(O, r, angleA, angleB);
    draw(anglePath, p);

    // Position the label
    real midAngle = (angleA + angleB) / 2;
    pair labelPos = O + (r + 8) * (cos(radians(midAngle)), sin(radians(midAngle)));
    label(label, labelPos);
}

