// --- Helper: right angle marker ---
void rightanglemark(pair A, pair B, pair C, real size=0.4, pen p=black) {
  // Draws a right angle marker at point B between BA and BC
  pair BA = unit(A-B), BC = unit(C-B);
  pair p1 = B + BA*size, p2 = p1 + BC*size;
  draw(B--p1--p2--(B + BC*size), p);
}

// --- Helper: congruence tick mark ---
void tickmark(pair P, pair Q, real len=0.18, pen p=black) {
  pair M = (P+Q)/2;
  pair dir = rotate(90)*(Q-P);
  draw(M-len*dir/(2*length(dir))--M+len*dir/(2*length(dir)), p);
}

