import geometry;

// Set up the figure
unitsize(1.5cm);
defaultpen(fontsize(10pt));

// Define colors
pen exprcolor = rgb(0.2, 0.2, 0.8); // Dark blue color for expressions

// Define the coordinates for the circle's center (C) and the external point (A)
pair C = (0, 0);
pair A = (3.2, -1.5); // Place A to the right of the circle

// Define circle radius
real radius = 1.5; // 3cm minimum size / 2 = 1.5cm radius

// Draw the circle
draw(circle(C, radius), black + 1bp);

// Draw the center point as a filled dot and add its label 'C'
dot(C, black + 3bp);
label("C", C, NW);

// Find the tangency points using the correct geometric method
// For tangent lines from external point A to circle with center C and radius r,
// we need to find points on the circle where the line from A is perpendicular to the radius

pair AC = A - C;
real d = length(AC); // Distance from A to C

// The tangent points lie on the circle of intersection between:
// 1. The original circle centered at C with radius r
// 2. A circle with diameter AC (since tangent makes right angle with radius)

// Midpoint and radius of the auxiliary circle
pair midpoint = (A + C) / 2;
real aux_radius = d / 2;

// Declare the tangent points
pair D, B;

// Calculate intersection points
real a = radius^2 - aux_radius^2 + (d/2)^2;
real discriminant = radius^2 - (a^2)/(d^2);

if (discriminant >= 0) {
    real h = sqrt(discriminant);
    pair perpendicular = (-AC.y, AC.x) / d; // Unit vector perpendicular to AC

    // Two tangent points
    pair intersection_base = C + (a/d^2) * AC;
    D = intersection_base + h * perpendicular;   // Upper tangent point
    B = intersection_base - h * perpendicular;   // Lower tangent point
} else {
    // Fallback (shouldn't happen for external point)
    D = C + (radius, 0);
    B = C + (-radius, 0);
}

// Draw the tangent lines from the external point A to the tangency points B and D
draw(A--D, black + 1bp);
draw(A--B, black + 1bp);

// Add labels for the points A, B, and D
label("A", A, E);
label("D", D, N);
label("B", B, S);

// Add the algebraic expressions as labels on the tangent lines
// Place them at the midpoints of the tangent lines
label("$2x + 5$", (A + D)/2, NE, exprcolor);
label("$3x^2 + 2x - 7$", (A + B)/2, S, exprcolor);

