real radius = 2.2;
real xchord = -0.8; // x-coordinate for the vertical chord MP

// Calculate the y-coordinate for points M and P on the circle
real ychord = sqrt(radius^2 - xchord^2);

// Define coordinates for all points in a horizontal/vertical setup
pair Q = (0, 0);
pair L = (-radius, 0);
pair N = (radius, 0);
pair M = (xchord, ychord);
pair P = (xchord, -ychord);
pair I = (xchord, 0); // Intersection point

// Apply rotation to all points
real rotAngle = -15;
transform t = rotate(rotAngle);
Q = t * Q;
L = t * L;
N = t * N;
M = t * M;
P = t * P;
I = t * I;

// Draw the circle
draw(circle(Q, radius));

// Draw the chords LN (diameter) and MP
draw(L -- N);
draw(M -- P);

// Draw the center point Q
dot(Q, linewidth(2.4pt));

// Draw the right-angle marker at the intersection I
void drawRightAngleMarker(pair center, real size = 0.3) {
    pair corner1 = center + (size, 0);
    pair corner2 = center + (size, size);
    pair corner3 = center + (0, size);

    draw(corner1 -- corner2 -- corner3, red + linewidth(0.5pt));
}

// Apply the right angle marker with rotation
pair rightAngleDir1 = t * (1, 0);
pair rightAngleDir2 = t * (0, 1);
pair corner1 = I + 0.3 * rightAngleDir1;
pair corner2 = I + 0.3 * rightAngleDir1 + 0.3 * rightAngleDir2;
pair corner3 = I + 0.3 * rightAngleDir2;
draw(corner1 -- corner2 -- corner3, rgb(0.6, 0.2, 0.2) + linewidth(0.5pt));

// Add labels for the points (counter-rotate to keep text upright)
label("$M$", M, rotate(-rotAngle) * NW, fontsize(10pt));
label("$P$", P, rotate(-rotAngle) * SW, fontsize(10pt));
label("$L$", L, rotate(-rotAngle) * W, fontsize(10pt));
label("$N$", N, rotate(-rotAngle) * E, fontsize(10pt));
label("$Q$", Q, rotate(-rotAngle) * N, fontsize(10pt));

// Add labels for the lengths and arrows pointing to the segments
// Position labels in the rotated coordinate system
pair labelM = t * (1.5, 1.1);
pair labelP = t * (1.5, -1.1);

// Add the length labels (counter-rotate to keep text upright)
label("$5x-6$", labelM, rotate(-rotAngle) * E, blue + fontsize(10pt));
label("$2x+9$", labelP, rotate(-rotAngle) * E, blue + fontsize(10pt));

// Draw arrows from labels to midpoints of chord segments
pair midMI = (M + I) / 2;
pair midPI = (P + I) / 2;

draw(labelM -- midMI, blue + linewidth(0.5pt), Arrow(size=3pt));
draw(labelP -- midPI, blue + linewidth(0.5pt), Arrow(size=3pt));

