pen fillcolor = rgb(0.7*256, 1*256, 1*256); // cyan!30
pen blackpen = black + 1;    // thick black
pen redpen = rgb(0.8*256, 0*256, 0*256); // red!80!black

// Helper function for elliptical arc
path ellipticalarc(pair center, real rx, real ry, real startdeg, real enddeg) {
    path p;
    int n = 50;
    real step = (enddeg - startdeg) / n;

    for (int i = 0; i <= n; ++i) {
        real angle = radians(startdeg + i * step);
        pair pt = center + (rx * cos(angle), ry * sin(angle));
        if (i == 0) {
            p = pt;
        } else {
            p = p -- pt;
        }
    }
    return p;
}

// 1. Draw the main shape (filled)
// Create the main outline: top line, right semicircle, bottom line, left semicircle
path topline = (0, R) -- (L, R);
path rightsemi = ellipticalarc((L, 0), R, R, 90, -90);
path bottomline = (L, -R) -- (0, -R);
path leftsemi = ellipticalarc((0, 0), R, R, 270, 90);

path mainshape = topline -- rightsemi -- bottomline -- leftsemi -- cycle;
fill(mainshape, fillcolor);
draw(mainshape, blackpen);

// 2. Draw 3D perspective cues
// Back of the cylinder's circular bases (solid lines)
draw(ellipticalarc((0, 0), P*R, R, -90, 90), blackpen + dashed);
draw(ellipticalarc((L, 0), P*R, R, 90, -90), blackpen + dashed);

// Front of the cylinder's circular bases (dashed lines)
draw(ellipticalarc((0, 0), P*R, R, 270, 90), blackpen);
draw(ellipticalarc((L, 0), P*R, R, 270, 90), blackpen);

// 3. Draw center points
dot((0, 0), black + 5);
dot((L, 0), black + 5);

// 4. Draw internal construction lines (in red)
// Central axis connecting the centers of the bases
draw((0, 0) -- (L, 0), redpen);

// Vertical lines representing the radii
draw((0, 0) -- (0, R), redpen);
draw((L, 0) -- (L, R), redpen);

// Small squares to indicate right angles
