import geometry;
import markers;

// Define the arc function first
path arc(pair center, real xrad, real yrad, real startangle, real endangle) {
    int n = 50;
    real dt = (endangle - startangle) / n;
    path p;
    for (int i = 0; i <= n; ++i) {
        real t = startangle + i * dt;
        real tr = t * pi / 180; // convert degrees to radians
        pair pt = center + (xrad * cos(tr), yrad * sin(tr));
        if (i == 0) p = pt;
        else p = p -- pt;
    }
    return p;
}

