pair B = dir(angleStart + rotateAngle) * innerR;
pair C = dir(angleEnd + rotateAngle) * outerR;
pair D = dir(angleStart + rotateAngle) * outerR;

// --- FILL ONLY THE SECTOR BETWEEN ARCS AB AND CD ---
path sector = A--C
  .. arc(O, outerR, angleEnd + rotateAngle, angleStart + rotateAngle)
  --B
  .. arc(O, innerR, angleStart + rotateAngle, angleEnd + rotateAngle)
  --cycle;
fill(sector, gray(0.7));

// --- DRAW CIRCLES AND RADIAL LINES ---
draw(Circle(O, innerR));
draw(Circle(O, outerR));
draw(O--C);
draw(O--D);

// --- LABELS ---
dot(O);
label("$O$", O, SW);
label("$A$", A, dir(120 + rotateAngle));
label("$B$", B, dir(-45 + rotateAngle));
label("$C$", C, N);
label("$D$", D, E);


// --- DRAW THE MAIN SHAPES ---
// Draw the two concentric circles
draw(Circle(O, innerR));
draw(Circle(O, outerR));

// Draw the radial lines that bound the sector
