import math;

// Set up the figure
unitsize(1cm);

// Style definitions
pen packBoxPen = gray(0.8);
pen circlePen = gray(0.8);
pen crossOutPen = black + 1.5bp;
pen dashedOutlinePen = black + dashed + 1bp;

// Function to draw a ten pack
void drawTenPack(pair center) {
    // Draw the rounded rectangle box
    path box = box(center + (-0.9, -0.4), center + (0.9, 0.4));
    draw(box, packBoxPen);

    // Draw the 2x5 grid of circles
    for (int row = 0; row < 2; ++row) {
        for (int col = 0; col < 5; ++col) {
            real x = center.x + (col - 2) * 0.3;
            real y = center.y + (row - 0.5) * 0.3;
            draw(circle((x, y), 0.15), circlePen);
        }
    }
}

// Function to draw a cross-out line
void crossOut(pair center, real width = 1.8, real height = 0.8) {
    draw((center.x - width/2, center.y - height/2) -- 
         (center.x + width/2, center.y + height/2), crossOutPen);
}

// Function to draw a small circle
void drawSmallCircle(pair center) {
    draw(circle(center, 0.15), circlePen);
}

// Function to cross out a small circle
void crossOutCircle(pair center) {
    draw((center.x - 0.15, center.y - 0.15) -- 
         (center.x + 0.15, center.y + 0.15), crossOutPen);
}

// Main drawing

// 1. Draw the 2x6 grid of ten-packs
pair[][] packCenters = new pair[2][6];
for (int j = 0; j < 2; ++j) {
    for (int i = 0; i < 6; ++i) {
        real xpos = i * 2.2;
        real ypos = -j * 1.2;
        pair center = (xpos, ypos);
        packCenters[j][i] = center;
        drawTenPack(center);
    }
}

// 2. Draw the extra circles to the right of the grid
pair rightEdge = packCenters[0][5] + (0.9, 0);
pair extraStart = rightEdge + (0.5, 0);

// Top row of extra circles
pair[] extraCircles = new pair[5];
for (int i = 0; i < 5; ++i) {
    extraCircles[i] = extraStart + (i * 0.3, 0.2);
    drawSmallCircle(extraCircles[i]);
}

// Cross out the first circle in the top row
crossOutCircle(extraCircles[0]);

// Bottom row of extra circles (2 circles, both crossed out)
pair[] extraLowerCircles = new pair[2];
for (int i = 0; i < 1; ++i) {
    extraLowerCircles[i] = extraStart + (i * 0.3, -0.1);
    drawSmallCircle(extraLowerCircles[i]);
    crossOutCircle(extraLowerCircles[i]);
}

// 3. Cross out specified packs
crossOut(packCenters[0][5]); // p-1-6
crossOut(packCenters[1][5]); // p-2-6
crossOut(packCenters[1][4]); // p-2-5

// 4. Draw the large dashed outline
pair topLeft = packCenters[0][0] + (-0.9, 0.4) + (-0.2, 0.6);
pair bottomRight = packCenters[1][4] + (0.9, -0.4) + (0.2, -0.6);
path outline = box(topLeft, bottomRight);
draw(outline, dashedOutlinePen);

