// Function to create rotated wings
void drawWing(real angle) {
    // Apply rotation transformation
    transform t = rotate(angle);

    // Define Wing and Tab Coordinates (before rotation)
    pair W_C2 = Rinner * dir(210);
    pair W_C3 = Rinner * dir(330);
    pair W_P = Router * dir(270);

    // Coordinates for the tab's base on the wing's edge
    pair T_start_base = W_C3 + 0 * (W_P - W_C3);
    pair T_end_base = W_C3 + 0.50 * (W_P - W_C3);

    // Coordinates for the tab's outer corners
    pair T_outer_1 = T_start_base + 0.6 * dir(-60);
    pair T_outer_2 = T_end_base + 0.5 * dir(-10);

    // Apply rotation to all coordinates
    W_C2 = t * W_C2;
    W_C3 = t * W_C3;
    W_P = t * W_P;
    T_start_base = t * T_start_base;
    T_end_base = t * T_end_base;
    T_outer_1 = t * T_outer_1;
    T_outer_2 = t * T_outer_2;

    // Draw Wing Edges with Dashed Tab Base
    draw(W_C2--W_P);
    draw(W_P--T_end_base);
    draw(T_end_base--T_start_base, dashed);
    draw(T_start_base--W_C3);

    // Fill and Draw the Hatching Tab
    path tabPath = T_start_base--T_outer_1--T_outer_2--T_end_base--cycle;
    fill(tabPath, pattern("northeast"));
    draw(T_start_base--T_outer_1--T_outer_2--T_end_base);

    // Hole
    pair M_base = 0.5 * (W_C2 + W_C3);
    pair Hole_center = W_P + 0.2 * (M_base - W_P);
    draw(circle(Hole_center, 0.15));
}

// Draw rotated wings
for (int i = 0; i < 3; ++i) {
    drawWing(i * 120);
}

