// Function to draw double tick marks at the midpoint of a line segment
void drawSideTicks(pair A, pair B) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = (-dir.y, dir.x); // perpendicular direction
    real tickLength = 0.15;
    real tickSeparation = 0.08;

    draw(mid - tickSeparation * dir - tickLength * perp -- 
         mid - tickSeparation * dir + tickLength * perp, black + linewidth(1.5));
    draw(mid + tickSeparation * dir - tickLength * perp -- 
         mid + tickSeparation * dir + tickLength * perp, black + linewidth(1.5));
}

