    real t = i / numPoints;
    real angle1 = atan2(dir1.y, dir1.x);
    real angle2 = atan2(dir2.y, dir2.x);

    // Handle angle wraparound
    if (angle2 - angle1 > pi) angle1 += 2*pi;
    if (angle1 - angle2 > pi) angle2 += 2*pi;

    real currentAngle = (1-t) * angle1 + t * angle2;
    triple arcPoint = center + angleRadius * (cos(currentAngle), sin(currentAngle), 0);

    if (i == 0) {
        angleArc = arcPoint;
    } else {
        angleArc = angleArc -- arcPoint;
    }
}

