// Function to draw single tick mark at midpoint of segment
void drawSingleTick(pair A, pair B) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = (-dir.y, dir.x);
    real tick_size = 0.08;
    draw((mid - tick_size * perp)--(mid + tick_size * perp), linewidth(0.6));
}

// Function to draw double tick marks at midpoint of segment
void drawDoubleTick(pair A, pair B) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = (-dir.y, dir.x);
    real tick_size = 0.08;
    real spacing = 0.04;
    draw((mid - tick_size * perp - spacing * dir)--(mid + tick_size * perp - spacing * dir), linewidth(0.6));
    draw((mid - tick_size * perp + spacing * dir)--(mid + tick_size * perp + spacing * dir), linewidth(0.6));
}

// Add congruence tick marks
// Single tick marks
drawSingleTick(G, J);
drawSingleTick(K, L);

// Double tick marks
drawDoubleTick(G, I);
drawDoubleTick(K, M);
drawDoubleTick(I, M);

// Function to draw angle arc with label
void drawAngle(pair A, pair B, pair C, string angleLabel, real radius, bool arrow = false) {
    pair dir1 = unit(A - B);
    pair dir2 = unit(C - B);
    real angle1 = degrees(dir1);
    real angle2 = degrees(dir2);

    // Ensure we draw the smaller angle
    if (abs(angle2 - angle1) > 180) {
        if (angle2 > angle1) angle1 += 360;
        else angle2 += 360;
    }

    // Draw the arc
    path arc_path = arc(B, radius, angle1, angle2);
    if (arrow) {
        draw(arc_path, arrow=Arrow(TeXHead));
    } else {
        draw(arc_path);
    }

    // Calculate label position
    real mid_angle = (angle1 + angle2) / 2;
    pair label_pos = B + 1.5 * radius * (cos(radians(mid_angle)), sin(radians(mid_angle)));
    label(angleLabel, label_pos);
}

