import graph;
size(10cm);

// --- 1. Define Hyperbola Parameters ---
real h = 1;
real k = 1;
real a = sqrt(2);
real b = sqrt(3);
real c = sqrt(5);

// --- 2. Define Colors ---
pen hyperbolablue = rgb(58/255, 109/255, 163/255);  // #3A6DA3
pen focicyan = rgb(0/255, 169/255, 157/255);        // #00A99D

// --- 3. Draw Coordinate Axes ---
draw((-2.8, 0)--(4.8, 0), gray + 1.2bp, Arrow(TeXHead));
label("$x$", (4.8, 0), SW);

draw((0, -2.5)--(0, 4), gray + 1.2bp, Arrow(TeXHead));
label("$y$", (0, 4), SW);

// --- 4. Draw the Hyperbola ---
real tmin = -1.6, tmax = 1.6;
int samples = 150;

// Function to create parametric path
path createBranch(real sign) {
    guide g;
    for(int i = 0; i <= samples; ++i) {
        real t = tmin + i * (tmax - tmin) / samples;
        real x = h + sign * a * cosh(t);
        real y = k + b * sinh(t);
        g = g .. (x, y);
    }
    return g;
}

// Draw both branches
draw(createBranch(1), hyperbolablue + 1.2bp);   // Right branch
draw(createBranch(-1), hyperbolablue + 1.2bp);  // Left branch

// --- 5. Add arrows to the ends of the hyperbola branches ---
real t1 = 1.5, t2 = 1.6;

// Calculate arrow positions
pair r1 = (h + a*cosh(t1), k + b*sinh(t1));
pair r2 = (h + a*cosh(t2), k + b*sinh(t2));
pair r3 = (h + a*cosh(t1), k - b*sinh(t1));
pair r4 = (h + a*cosh(t2), k - b*sinh(t2));
pair l1 = (h - a*cosh(t1), k + b*sinh(t1));
pair l2 = (h - a*cosh(t2), k + b*sinh(t2));
pair l3 = (h - a*cosh(t1), k - b*sinh(t1));
pair l4 = (h - a*cosh(t2), k - b*sinh(t2));

// Draw arrows
draw(r1--r2, hyperbolablue + 1.2bp, Arrow(TeXHead));
draw(r3--r4, hyperbolablue + 1.2bp, Arrow(TeXHead));
draw(l1--l2, hyperbolablue + 1.2bp, Arrow(TeXHead));
draw(l3--l4, hyperbolablue + 1.2bp, Arrow(TeXHead));

// --- 6. Plot and Label Points ---

// Center (1, 1)
pair center = (h, k);
filldraw(circle(center, 0.06), red, red);
label("Center" + "\\" + "$(1, 1)$", center, S, red);

// Vertices (h±a, k)
pair vL = (h - a, k);
pair vR = (h + a, k);
filldraw(circle(vL, 0.06), hyperbolablue, hyperbolablue);
filldraw(circle(vR, 0.06), hyperbolablue, hyperbolablue);
label("$(1-\sqrt{2}, 1)$", vL, N);
label("$(1+\sqrt{2}, 1)$", vR, N);

// "Vertices" label with arrows pointing to the vertex points
pair vertices_label = (1, 3.2);
label("Vertices", (1, 3.4));
draw(vertices_label{dir(-135)}..{dir(-90)}vL, gray(0.5) + 0.8bp, Arrow(TeXHead));
draw(vertices_label{dir(-45)}..{dir(-90)}vR, gray(0.5) + 0.8bp, Arrow(TeXHead));

// Foci (h±c, k)
pair fL = (h - c, k);
pair fR = (h + c, k);
filldraw(circle(fL, 0.06), focicyan, focicyan);
filldraw(circle(fR, 0.06), focicyan, focicyan);
label("Foci" + "\\" + "$(1-\sqrt{5}, 1)$", fL, SW, focicyan);
label("Foci" + "\\" + "$(1+\sqrt{5}, 1)$", fR, SE, focicyan);

