// Function to draw single tick mark
void drawTick1(pair A, pair B, pen p = red + linewidth(1.2)) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = I * dir; // Perpendicular direction
    real ticksize = 0.08;
    draw(mid - ticksize * perp -- mid + ticksize * perp, p);
}

// Function to draw double tick marks
void drawTick2(pair A, pair B, pen p = red + linewidth(1.2)) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = I * dir; // Perpendicular direction
    real ticksize = 0.08;
    real spacing = 0.04;
    draw(mid - spacing * dir - ticksize * perp -- mid - spacing * dir + ticksize * perp, p);
    draw(mid + spacing * dir - ticksize * perp -- mid + spacing * dir + ticksize * perp, p);
}

// Function to draw faded double tick marks
void drawTick2Faded(pair A, pair B) {
    pen fadedpen = red + linewidth(1.2);
    drawTick2(A, B, fadedpen);
}

// Function to draw triple tick marks
void drawTick3(pair A, pair B, pen p = red + linewidth(1.2)) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = I * dir; // Perpendicular direction
    real ticksize = 0.08;
    real spacing = 0.06;
    draw(mid - spacing * dir - ticksize * perp -- mid - spacing * dir + ticksize * perp, p);
    draw(mid - ticksize * perp -- mid + ticksize * perp, p);
    draw(mid + spacing * dir - ticksize * perp -- mid + spacing * dir + ticksize * perp, p);
}

