// --- DRAW DIMENSION LINES ---
// Helper for dimension lines with ticks and labels
void dimline(pair A, pair B, pair offset, string lbl, bool below=false) {
  pair A1 = A + offset, B1 = B + offset;
  draw(A1{dir(90)}..A1..A1, EndArrow(HookHead));
  draw(B1{dir(90)}..B1..B1, EndArrow(HookHead));
  draw(A1--B1, black+1bp+linewidth(0.6bp));
  label(lbl, 0.5*(A1+B1) + (below ? (0,-1.5) : (0,1.5)), below ? S : N, fontsize(10pt));
}


// --- HELPER FUNCTION FOR DIMENSION LINES WITH TICKS ---
void dimline(pair A, pair B, pair offset, string lbl, bool below=false) {
  pair A1 = A + offset, B1 = B + offset;
  pair dir = unit(B1 - A1);
  pair norm = rotate(90)*dir;
  // Draw main dimension line
  draw(A1--B1, black+1bp+linewidth(0.6bp));
  // Draw ticks at delimiters
  real ticklen = 0.5;
  draw(A1 - norm*ticklen/2 -- A1 + norm*ticklen/2, black+1bp);
  draw(B1 - norm*ticklen/2 -- B1 + norm*ticklen/2, black+1bp);
  // Draw label
  label(lbl, 0.5*(A1+B1) + (below ? (0,-1.5) : (0,1.5)), below ? S : N, fontsize(10pt));
}

