pair Q1 = Q + (symbolsize, 0);
pair Q2 = Q + (0, symbolsize);
pair Q3 = Q + (symbolsize, symbolsize);
draw(Q1 -- Q3 -- Q2, symbol_pen);

// Right-angle symbol at S (for angle QSP)
pair SQ_unit = unit(Q - S);
pair SP_unit = unit(P - S);
pair V1 = S + symbolsize * SQ_unit;
pair V2 = S + symbolsize * SP_unit;
pair V3 = V1 + V2 - S;
