real offset = 1;  // Reduced offset distance
real ticklen = 0.15;  // Smaller tick length

// Calculate perpendicular direction to hypotenuse RP
pair RP_perp = unit(rotate(90) * (P - R));

// Define key points on the dimension line, parallel to the hypotenuse
pair d_R = R + offset * RP_perp;
pair d_S = S + offset * RP_perp;
