pen linecolor = rgb(139, 0, 0);      // Dark red for measurement lines
pen blackpen = black + 1;            // thick black
pen redpen = linecolor + 0.8;        // semithick red

// Helper function for elliptical arc
path ellipticalarc(pair center, real rx, real ry, real startdeg, real enddeg) {
    path p;
    int n = 50;
    real step = (enddeg - startdeg) / n;

    for (int i = 0; i <= n; ++i) {
        real angle = radians(startdeg + i * step);
        pair pt = center + (rx * cos(angle), ry * sin(angle));
        if (i == 0) {
            p = pt;
        } else {
            p = p -- pt;
        }
    }
    return p;
}

// Helper function for semicircle
path semicircle(pair center, real radius) {
    path p;
    int n = 50;

    for (int i = 0; i <= n; ++i) {
        real angle = radians(180 - i * 180.0 / n);
        pair pt = center + (radius * cos(angle), radius * sin(angle));
        if (i == 0) {
            p = pt;
        } else {
            p = p -- pt;
        }
    }
    return p;
}

