// The hemisphere fill (top dome)
path topdome = semicircle(C, 6 * scale);
path backbase = ellipticalarc(C, 6 * scale, baseyrad, 180, 360);
path hemispherefill = L -- topdome -- reverse(backbase) -- cycle;
fill(hemispherefill, fillcolor);

// 2. Draw the black outlines of the composite shape
