// Function to draw angle arcs and labels
void drawAngle(pair vertex, pair point1, pair point2, string label, real radius = 2.5) {
    real angle1 = degrees(point1 - vertex);
    real angle2 = degrees(point2 - vertex);

    // Ensure we draw the smaller angle
    if (abs(angle2 - angle1) > 180) {
        if (angle2 > angle1) angle1 += 360;
        else angle2 += 360;
    }

    path arc = arc(vertex, radius, angle1, angle2);
    draw(arc);

    // Position label
    real midAngle = (angle1 + angle2) / 2;
    pair labelPos = vertex + 1.3 * radius * dir(midAngle);
    label(label, labelPos);
}

