if (angle_JKG > 180) angle_JKG = 360 - angle_JKG;

real bisector_angle_K = degrees(atan2(dir_KJ.y, dir_KJ.x)) + degrees(atan2(dir_KG.y, dir_KG.x) - atan2(dir_KJ.y, dir_KJ.x)) / 2;
pair label_pos_K = K + 0.6 * angle_radius * (cos(radians(bisector_angle_K)), sin(radians(bisector_angle_K)));
