// Function to draw right angle marker
void drawRightAngle(pair vertex, pair dir1, pair dir2, real size = 0.45) {
    pair corner1 = vertex + size * unit(dir1);
    pair corner2 = vertex + size * unit(dir2);
    pair corner3 = vertex + size * unit(dir1) + size * unit(dir2);
    draw(corner1--corner3--corner2);
}

