// Function to draw single tick mark at midpoint of segment
void drawSingleTick(pair A, pair B, real tick_size = 0.15) {
    pair mid = (A + B) / 2;
    pair dir = unit(B - A);
    pair perp = (-dir.y, dir.x);
    draw((mid - tick_size * perp)--(mid + tick_size * perp));
}

// Tick marks for congruent segments BK and HG
