import graph;
import patterns;

// Set up the figure
unitsize(0.045cm); // Scale the drawing (1 unit = 1 meter)
defaultpen(fontsize(10pt));

// Define colors from image
pen towerfill = rgb(0.992, 0.886, 0.776);      // #FDE2C6
pen towerstroke = rgb(0.906, 0.561, 0.298);    // #E78F4C
pen supportcolor = rgb(0.4, 0.761, 0.761);     // #66C2C2

// Define tower parameters from the figure
real dTop = 72;         // Diameter at the top
real dWaist = 60;       // Diameter at the waist
real dBase = 140;       // Diameter at the start of the vertical base
real hTotal = 179.6;    // Total height of the entire structure
real hTop = 79.6;       // Height from waist to top
real hPillarSection = 20; // The height of the pillar section at the bottom

// Calculate coordinates and hyperbola parameters
real a = dWaist / 2;        // Hyperbola's 'a' is the waist radius
real rTop = dTop / 2;       // Radius at the top
real rBase = dBase / 2;     // Radius at the top of the pillar section

real yTop = hTop;           // y-coordinate of the top
real yAbsBottom = yTop - hTotal; // y-coordinate of the absolute bottom
real yShellEnd = yAbsBottom + hPillarSection; // y-coord where the shell ends and pillars begin

// b parameter for top part, calculated to match the given top radius
real bTop = yTop / sqrt((rTop/a)^2 - 1);
// b parameter for bottom part, calculated to match the desired base radius
real bBottom = abs(yShellEnd) / sqrt((rBase/a)^2 - 1);

// Function to get radius 'x' for a given height 'y', using the appropriate 'b'
real hyperbolaX(real y) {
    if (y > 0) {
        return a * sqrt(1 + (y/bTop)^2);
    } else {
        return a * sqrt(1 + (y/bBottom)^2);
    }
}

// Create hyperbola curves
int samples = 100;
real[] yvals = new real[samples];
pair[] leftCurve = new pair[samples];
pair[] rightCurve = new pair[samples];

for (int i = 0; i < samples; ++i) {
    yvals[i] = yTop + (yShellEnd - yTop) * i / (samples - 1);
    real x = hyperbolaX(yvals[i]);
    leftCurve[i] = (-x, yvals[i]);
    rightCurve[i] = (x, yvals[i]);
}

// Draw tower body - fill the complete shape
path towerShape = 
    // Left side curve
    graph(leftCurve, operator ..) --
    // Bottom left corner
    (-hyperbolaX(yShellEnd), yAbsBottom) --
    // Bottom right corner
    (hyperbolaX(yShellEnd), yAbsBottom) --
    // Right side curve (reversed)
    reverse(graph(rightCurve, operator ..)) --
    cycle;

fill(towerShape, towerfill);

// Draw the curved internal "support" lines ONLY on the left for the cutaway view
for (int j = 1; j <= 9; ++j) {
    real factor = j * 0.1;
    pair[] internalCurve = new pair[samples];
    for (int i = 0; i < samples; ++i) {
        real x = hyperbolaX(yvals[i]);
        internalCurve[i] = (-factor * x, yvals[i]);
    }
    draw(graph(internalCurve, operator ..), towerstroke + opacity(0.6) + linewidth(0.5));
}

// Draw the outline of the tower
draw(graph(leftCurve, operator ..), towerstroke + linewidth(1.2));  // Left curved edge
draw(graph(rightCurve, operator ..), towerstroke + linewidth(1.2)); // Right curved edge
draw((-hyperbolaX(yTop), yTop) -- (hyperbolaX(yTop), yTop), towerstroke + linewidth(1.2)); // Top edge
draw((0, yAbsBottom) -- (0, yTop), towerstroke + linewidth(1.2) + dashed); // Centerline

// Draw base structure (cutaway)
// Right side: Solid vertical wall
draw((hyperbolaX(yShellEnd), yShellEnd) -- (hyperbolaX(yShellEnd), yAbsBottom), towerstroke + linewidth(1.2));

// Bottom edge
draw((-hyperbolaX(yShellEnd), yAbsBottom) -- (hyperbolaX(yShellEnd), yAbsBottom), towerstroke + linewidth(1.2));

// Horizontal ring where shell meets pillars
draw((-hyperbolaX(yShellEnd), yShellEnd) -- (hyperbolaX(yShellEnd), yShellEnd), towerstroke + linewidth(1.2));

// Left side: Draw the teal support pillars and V-shaped connectors
int numSupports = 15; // Number of supports on the left half
real rShellEnd = hyperbolaX(yShellEnd); // Get radius at shell end
for (int i = 0; i <= numSupports; ++i) {
    real xpos = -i * rShellEnd / numSupports;
    // V-shaped connector
    draw((xpos, yShellEnd) -- (xpos, yShellEnd - 7), supportcolor + linewidth(1.2));
    // Vertical pillar (drawn as a filled rectangle)
    fill(box((xpos - 1.8, yShellEnd - 7), (xpos + 1.8, yAbsBottom)), supportcolor);
}

// Function to draw dimension lines with T-ends
void drawDimLine(pair start, pair end, string label, pair labelpos, pen p = currentpen) {
    draw(start -- end, p + linewidth(0.7));

    // Draw T-ends (bars)
    pair dir = unit(end - start);
    pair perp = I * dir;
    real barwidth = 2;
    draw(start - barwidth * perp -- start + barwidth * perp, p + linewidth(0.7));
    draw(end - barwidth * perp -- end + barwidth * perp, p + linewidth(0.7));

    // Add label
    label(label, labelpos, p);
}

// Draw dimension lines and labels
