path wavyline(pair start, pair end, real amplitude=1, int segments=60) {
    path p = start;

    for(int i = 1; i <= segments; ++i) {
        real t = i / segments;
        pair basepoint = start + t * (end - start);

        // Calculate perpendicular offset
        pair direction = end - start;
        pair perpendicular = (-direction.y, direction.x);
        if(length(perpendicular) > 0) {
            perpendicular = perpendicular / length(perpendicular);
        }

        // Apply sinusoidal offset
        real offset = amplitude * sin(2 * pi * t * 9); // 3 cycles along the path
        pair wavy_point = basepoint + offset * perpendicular;

        p = p -- wavy_point;
    }
    return p;
}


