// --- Side Markings (Ticks) ---
void singletick(pair A, pair B, real len=0.18)
{
  // Draws a single tick at the midpoint of AB, sloped
  pair M = (A+B)/2;
  pair dir = rotate(90)*(B-A)/abs(B-A);
  draw(M - len*dir/2 -- M + len*dir/2, linewidth(0.7));
}

void doubletick(pair A, pair B, real len=0.18, real sep=0.09)
{
  // Draws two parallel ticks at the midpoint of AB, sloped
  pair M = (A+B)/2;
  pair dir = rotate(90)*(B-A)/abs(B-A);
  draw(M - sep*dir + len*dir/2 -- M - sep*dir - len*dir/2, linewidth(0.7));
  draw(M + sep*dir + len*dir/2 -- M + sep*dir - len*dir/2, linewidth(0.7));
}

// ABC: AB single, BC double
singletick(A,B);
doubletick(B,C);

// MKL: MK double, KL single
doubletick(M,K);
singletick(K,L);

// --- Angle Arcs at B and K ---
void anglearc(pair O, pair A, pair B, real r=0.38, pen p=black)
{
  real t1 = degrees(dir(A-O));
  real t2 = degrees(dir(B-O));
  real sweep = t2-t1;
  if(sweep <= 0) sweep += 360;
  draw(arc(O, r, t1, t1+sweep), p);
}

// At B: angle ABC
anglearc(B, A, C, 0.38, thick);

// At K: angle MKL
anglearc(K, M, L, 0.38, thick);

