label("F", F, SE, fontsize(10pt));

// Function to draw angle arc with label
void drawAngleLabel(pair vertex, pair point1, pair point2, string angleLabel, pen color = myblue) {
    real angle1 = degrees(atan2(point1.y - vertex.y, point1.x - vertex.x));
    real angle2 = degrees(atan2(point2.y - vertex.y, point2.x - vertex.x));

    // Ensure we draw the smaller angle
    if (abs(angle2 - angle1) > 180) {
        if (angle2 > angle1) {
            angle2 -= 360;
        } else {
            angle1 -= 360;
        }
    }

    real arcRadius = 0.7;
    draw(arc(vertex, arcRadius, angle1, angle2), color + linewidth(0.5pt));

    // Position label at the middle of the arc
    real midAngle = (angle1 + angle2) / 2;
    pair labelPos = vertex + 1.5 * arcRadius * dir(midAngle);
    label(angleLabel, labelPos, color + fontsize(12pt));
}

// Draw angle labels
