// Helper function to create elliptical arcs (must be defined before use)
path arc(pair center, real a, real b, real angle1, real angle2) {
    path p;
    int n = 50;
    real dt = (angle2 - angle1) / n;
    for (int i = 0; i <= n; ++i) {
        real t = angle1 + i * dt;
        real x = center.x + a * cos(radians(t));
        real y = center.y + b * sin(radians(t));
        if (i == 0) {
            p = (x, y);
        } else {
            p = p -- (x, y);
        }
    }
    return p;
}

