// Function to draw angle arc with label
void drawAngle(pair A, pair B, pair C, string angleLabel, real radius) {
    pair dir1 = unit(A - B);
    pair dir2 = unit(C - B);
    real angle1 = degrees(dir1);
    real angle2 = degrees(dir2);

    // Ensure we draw the smaller angle
    if (abs(angle2 - angle1) > 180) {
        if (angle2 > angle1) angle1 += 360;
        else angle2 += 360;
    }

    // Draw the arc
    path arc_path = arc(B, radius, angle1, angle2);
    draw(arc_path);

    // Calculate label position
    real mid_angle = (angle1 + angle2) / 2;
    pair label_pos = B + 1.3 * radius * (cos(radians(mid_angle)), sin(radians(mid_angle)));
    label(angleLabel, label_pos);
}

