// Define arrow style
pen arrowpen = linewidth(1) + black;
arrowbar arrow = Arrow(TeXHead, size=0.5mm);

// Draw the 3x3 grid
for(int i = 0; i <= 3; ++i) {
    draw((i,0)--(i,3), linewidth(1.2));
    draw((0,i)--(3,i), linewidth(1.2));
}

// Place K in the center cell
pair center = (1.5, 1.5);
label("$K$", center);

// Define the 8 adjacent cell centers
pair[] targets = {
    (0.5, 2.5),  // top-left
    (1.5, 2.5),  // top-center
    (2.5, 2.5),  // top-right
    (0.5, 1.5),  // left
    (2.5, 1.5),  // right
    (0.5, 0.5),  // bottom-left
    (1.5, 0.5),  // bottom-center
    (2.5, 0.5)   // bottom-right
};

// Draw arrows from center to all 8 adjacent cells
for(pair target : targets) {
    // Calculate direction vector and shorten the arrow
    pair direction = unit(target - center);
    pair start = center + 0.15 * direction;  // shorten start
    pair end = target - 0.15 * direction;    // shorten end

    draw(start--end, arrowpen, arrow);
}

